#include <argp.h>#include <iostream>#include "termNote/NoteManager.hpp"static termnote::NoteManager note_manager;static int parseOptions(int key, char *arg,                        struct argp_state *state) {    switch (key)    {        case 'a':            note_manager.add_note(arg);            break;        case 'd':            note_manager.rem_note(std::stoi(arg));            break;        case 'l':            note_manager.write_notes(std::cout);            break;        case 's':            note_manager.write_note(std::cout, std::stoi(arg));            break;    }    return 0;}int main(int argc, char **argv) {    if (argc == 1)    {        note_manager.write_notes(std::cout);    }    else    {        constexpr struct argp_option options[] = {            {"add", 'a', "string", 0, "Add an entry"},            {"delete", 'd', "int", 0, "Delete [n]th entry"},            {"delete", 'd', "list", 0, "Delete [n]th entries"},            {"list", 'l', 0, 0, "List all entries"},            {"show", 's', "int", 0, "Show [n]th entry"},            { 0 }        };        struct argp argp = { options, parseOptions };        return argp_parse(&argp, argc, argv, 0, 0, 0);    }}